import { getFetcher } from '../fetcher';
import useSWR from 'swr';

function getUser() {
  const { data, error } = useSWR(`/api/auth`, getFetcher);
  return {
    user: data,
    isLoading: !error && !data,
    isError: error,
  };
}

export { getUser };
